mapper.select:
select resource_id as resourceId, resource_name as resourceName, type as type, url as url, resource_order as resourceOrder, parent_id as parentId, parent_ids as parent_ids, permision as permision, remark as remark from t_resource_info
(resource_id = #{pId} or parent_id = #{pId})
order by resource_order limit #{offset}, #{limit}
